import logging
import os

import bisect
from collections import Counter

script_dir = os.path.dirname(__file__)
script_name = os.path.splitext(os.path.basename(__file__))[0]

log_file = os.path.join(script_dir, f"logs/{script_name}.log")
logging.basicConfig(
    level=logging.WARNING,
    filename=log_file,
    filemode="w",
)

to_decode = ""
input_file = os.path.join(script_dir, f"inputs/{script_name}.txt")
lines = [line.rstrip("\n") for line in open(input_file)]
chars = lines[0]

counts = Counter(chars)


class Token:
    @classmethod
    def new_token(cls, left, right):
        new_token = cls(left.token + right.token, left.weight + right.weight)
        new_token.left = left
        new_token.right = right
        return new_token

    def leaf_node(self):
        return len(self.token) == 1

    def __init__(self, token: str, weight: int):
        self.token = token
        self.weight = weight
        self.left = None
        self.right = None

    def __lt__(self, other):
        if self.weight == other.weight:
            return self.token < other.token
        return self.weight < other.weight

    def __repr__(self):
        return f"{self.token}:{self.weight}"


tokens = [Token(token, weight) for token, weight in counts.items()]
tokens.sort()
while True:
    left = tokens[0]
    right = tokens[1]
    tokens = tokens[2:]
    token = Token.new_token(left, right)
    if len(tokens) == 0:
        break
    lo, hi = 0, len(tokens)
    while lo < hi:
        mid = (lo + hi) // 2
        if token.weight < tokens[mid].weight:
            hi = mid
        else:
            lo = mid + 1
    tokens.insert(lo, token)

base_token = token
assert len(chars) == base_token.weight

output = ""
i = 0
while i < len(to_decode):
    if to_decode[i] == "1":
        token = token.right
    else:
        token = token.left
    if token.leaf_node():
        output += token.token
        token = base_token
    i += 1

print(output)
